import React from 'react';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const {
    name,
    image,
    price,
    unit,
    originalPrice,
    availability,
    vendor,
    distance,
    rating
  } = product;

  const discount = originalPrice 
    ? Math.round(((originalPrice - price) / originalPrice) * 100) 
    : 0;

  const getAvailabilityClass = () => {
    if (availability === 'In Stock') return 'in-stock';
    if (availability === 'Low Stock') return 'low-stock';
    return 'out-of-stock';
  };

  return (
    <div className="product-card">
      {discount > 0 && (
        <span className="discount-badge">{discount}% OFF</span>
      )}
      
      <div className="product-image">
        <span className="product-emoji">{image}</span>
      </div>

      <div className="product-info">
        <h3 className="product-name">{name}</h3>
        
        <div className="product-price">
          <span className="current-price">‚Çπ{price}</span>
          <span className="price-unit">/{unit}</span>
          {originalPrice && (
            <span className="original-price">‚Çπ{originalPrice}</span>
          )}
        </div>

        <div className={`availability ${getAvailabilityClass()}`}>
          <span className="availability-dot"></span>
          {availability}
        </div>

        <div className="vendor-info">
          <span className="vendor-name">üè™ {vendor}</span>
          <span className="vendor-distance">üìç {distance}</span>
        </div>

        {rating && (
          <div className="product-rating">
            <span className="rating-star">‚≠ê</span>
            <span className="rating-value">{rating}</span>
          </div>
        )}

        <button className="add-to-cart-btn">
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
